commits.content:
import React, { useState } from 'react';

export default function SystemPreferences({
  title = "System Preferences",
  showSaveButton = true,
  onSave = null,
}) {
  const [maintenanceMode, setMaintenanceMode] = useState(false);
  const [retentionPeriod, setRetentionPeriod] = useState('30');

  const handleSaveChanges = () => {
    const settings = {
      maintenanceMode,
      retentionPeriod,
    };

    console.log("Saving changes:", settings);

    if (onSave) {
      onSave(settings);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-sm">
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200">
          <h1 className="text-2xl font-semibold text-gray-900">{title}</h1>
        </div>

        {/* Content */}
        <div className="p-6 space-y-8">
          <div className="space-y-6">
            {/* Maintenance Mode Section */}
            <div className="flex items-start justify-between p-6 bg-gray-50 rounded-lg">
              <div className="flex-1">
                <h2 className="text-lg font-medium text-gray-900 mb-2">
                  Maintenance Mode
                </h2>
                <p className="text-sm text-gray-600">
                  Temporarily disable user logins for maintenance. Admin only can access
                </p>
              </div>
              <button
                onClick={() => setMaintenanceMode(!maintenanceMode)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
                  maintenanceMode ? 'bg-blue-600' : 'bg-gray-300'
                }`}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    maintenanceMode ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
            </div>

            {/* Divider */}
            <div className="border-t border-gray-200"></div>

            {/* Data Retention Section */}
            <div className="space-y-4">
              <h2 className="text-lg font-medium text-gray-900">
                Data Retention Period for Logs
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {[
                  { value: '10', label: '10 Days' },
                  { value: '30', label: '30 Days' },
                  { value: '60', label: '60 Days' },
                  { value: 'unlimited', label: 'Unlimited' },
                ].map((option) => (
                  <label
                    key={option.value}
                    className="flex items-center space-x-3 cursor-pointer p-3 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    <div className="flex items-center">
                      <input
                        type="radio"
                        name="retention"
                        value={option.value}
                        checked={retentionPeriod === option.value}
                        onChange={(e) => setRetentionPeriod(e.target.value)}
                        className="h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                      />
                    </div>
                    <span className="text-sm font-medium text-gray-900">
                      {option.label}
                    </span>
                  </label>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Save Button */}
        {showSaveButton && (
          <div className="px-6 py-4 border-t border-gray-200 flex justify-end">
            <button
              onClick={handleSaveChanges}
              className="px-6 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors"
            >
              SAVE CHANGES
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
