+
+    return () => clearTimeout(timeout);
+  }, [location]);
+
+  return (
+    <div className="flex">
+      {/* Sidebar */}
+      {/* Include your SecuritySidebar component here */}
+      <SecuritySidebar
+        collapsed={false} // adjust according to your state
+        setCollapsed={() => {}}
+        active={location.pathname}
+        mobileOpen={false}
+        setMobileOpen={() => {}}
+      />
+
+      {/* Main content area */}
+      <main className="flex-1 p-6 overflow-auto">
+        {loading ? (
+          <>
+            <SkeletonCard />
+            <SkeletonCard />
+            <SkeletonCard />
+          </>
+        ) : (
+          <Outlet />
+        )}
+      </main>
+    </div>
+  );
+}

