import { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { getInvitation, submitInvitation, createInvitation } from '../services/api';

export const useVisitorForm = () => {
  const { token } = useParams();
  const location = useLocation();
  const [formData, setFormData] = useState({
    guestName: '',
    phoneNumber: '',
    email: '',
    date: '',
    hostName: '',
    houseNumber: '',
    carPlate: '',
    photo: null
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisitorMode, setIsVisitorMode] = useState(false);
  const [isSecurityMode, setIsSecurityMode] = useState(false);
  const [isResidentMode, setIsResidentMode] = useState(false);

  // Determine the mode based on route and token
  useEffect(() => {
    if (token) {
      setIsVisitorMode(true);
      fetchInvitationData();
    } else if (location.pathname.includes('security-checkin')) {
      setIsSecurityMode(true);
    } else {
      setIsResidentMode(true);
    }
  }, [token, location.pathname]);

  const fetchInvitationData = async () => {
    if (!token) return;
    
    setLoading(true);
    try {
      const data = await getInvitation(token);
      setFormData(prevData => ({
        ...prevData,
        ...data, // Prefill with partial data from resident
      }));
    } catch (err) {
      setError('Failed to load invitation data');
      console.error('Error fetching invitation:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (isVisitorMode && token) {
        // Visitor completing the invitation
        await submitInvitation(token, formData);
        alert('Registration completed successfully!');
      } else {
        // Resident creating invitation or security filling form
        await createInvitation(formData);
        if (isResidentMode) {
          alert('Invitation sent successfully!');
        } else {
          alert('Visitor registered successfully!');
        }
      }
    } catch (err) {
      setError('Failed to submit form');
      console.error('Error submitting form:', err);
    } finally {
      setLoading(false);
    }
  };

  return {
    formData,
    loading,
    error,
    isVisitorMode,
    isSecurityMode,
    isResidentMode,
    handleInputChange,
    handleSubmit
  };
};
