+          linkHref="/emergencypage"
+          icon={
+            <img
+              src="/911.svg"
+              alt="Emergency Icon"
+              className="w-6 h-6 object-contain"
+            />
+          }
+        />
+
+        <Card1
+          cardTitle="Total Active Users"
+          count={80}
+          link="View users"
+          linkHref="/userspage"
+          icon={
+            <img
+              src="/users.svg"
+              alt="Users Icon"
+              className="w-6 h-6 object-contain"
+            />
+          }
+        />
+      </div>
+
+      
+      <div className="mb-[30px]">
+        <Card2
+          label="Active Incidents"
+          value="3"
+          icon={<AlertTriangle className="h-6 w-6 text-red-600" />}
+          iconBg="bg-red-100"
